import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { auth, database} from '../../config/firebase'
import { signOut } from 'firebase/auth'
import { collection, getDocs } from 'firebase/firestore'
import Clients from './Clients'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


function Main_dash() {
    const [isShown,setIsShown]=useState(false)
    const [clients,setClients]=useState([])
    const clientsCollectionRef=collection(database,"client")
// it runs auto when the page is loaded
useEffect(()=>{
    const getClientsList= async ()=>{
        try{
        const data= await getDocs(clientsCollectionRef)
        const filteredData=data.docs.map(doc=>({
            ...doc.data(),id:doc.id
        }))
        setClients(filteredData)
        }catch(err){
            console.error(err)
        }
    }
    getClientsList()
}

)


    const history = useNavigate()

    const handleClick = async () =>{
        await signOut(auth).then(val=>{
            console.log(val,"val")
            history('/')
        })
    }
  return (
    <div className='bg-black h-screen w-screen text-white flex flex-col items-center justify-center'>
        <div>
            {
                clients.map((a)=>(

                    !isShown&&<div className='bg-white w-full h-64' >
                        <span>
                            <p>Nom Complete:</p>
                            <p>{`${a.firstName} ${a.lastName}`}</p>
                        </span>

                        <span>
                            <p>numero telephone</p>
                            <p>{a.phone}</p>
                        </span>

                        <p className='text-black hover:underline'>Click ici pour voir les autres info</p>
                        {isShown&&<Clients data={a}/>}
                    </div>
                ))

}




        </div>

    </div>
  )
}

export default Main_dash
