import {
  faFacebookSquare,
  faInstagramSquare,
  faTwitterSquare,
} from '@fortawesome/free-brands-svg-icons';
import { faAngleDown, faAngleUp } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
  const [hover, setHover] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleMouseIn = () => {
    setHover(true);
  };

  const handleMouseOut = () => {
    setHover(false);
  };

  useEffect(() => {
    if (hover) {
      const timeoutId = setTimeout(() => {
        setShowDropdown(true);
      }, 300);

      return () => {
        clearTimeout(timeoutId);
      };
    } else {
      setShowDropdown(false);
    }
  }, [hover]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    if (window.scrollY > 100) {
      setIsScrolled(true);
    } else {
      setIsScrolled(false);
    }
  };

  return (
    <section className='sticky'>
      {!isScrolled && (
        <aside className={`bg-slate-100 transform opacity-${!isScrolled ? 100 : 0} transition-opacity duration-300 fixed top-0 w-full`}>
          <span>

          </span>
          <span className="flex text-2xl mr-2">
            <FontAwesomeIcon
              className="p-1 hover:text-blue-400 duration-200"
              icon={faFacebookSquare}
            />
            <FontAwesomeIcon
              className="p-1 hover:text-blue-400 duration-200"
              icon={faTwitterSquare}
            />
            <FontAwesomeIcon
              className="p-1 hover:text-blue-400 duration-200"
              icon={faInstagramSquare}
            />
          </span>
        </aside>
      )}
      <aside
        className={`bg-gradient-to-r from-cyan-500 to-blue-500 flex justify-between items-center h-16 text-white ${
          isScrolled ? 'fixed top-0 duration-150' : 'fixed top-8 duration-150'
        } w-full shadow-md`}
      >
        <span className="ml-10 flex items-center">
          <img
            className="ml-3 pt-2 h-16 w-38"
            src="./images/logo_2.png"
            alt="./images/logo_0.png"
          />
          <img
            className="h-16 w-18"
            src="./images/logo_0.png"
            alt="./images/logo_0.png"
          />
          <img
            className="ml-3 pt-2 h-20 w-40"
            src="./images/logo_1.png"
            alt="./images/logo_0.png"
          />
        </span>
        <nav className="text-2xl flex flex-row items-center justify-between">
          <ul className="flex px-3 justify-between items-center mr-10 font-bold">
            <li className="p-3 hover:border-b-4 rounded duration-100">
              <Link to="/">Home</Link>
            </li>
            <li className="p-3 hover:border-b-4 rounded duration-100">
              <Link to="/fields">Notre metiers</Link>
            </li>
            <li
              className="p-3 hover:border-b-4 rounded duration-100 cursor-pointer"
              onMouseEnter={handleMouseIn}
              onMouseLeave={handleMouseOut}
            >
              <p>
                Metiers{' '}
                <FontAwesomeIcon
                  className="text-lg"
                  icon={showDropdown ? faAngleDown : faAngleUp}
                />{' '}
              </p>
              {showDropdown && (
                <span className="bg-gray-600 absolute flex flex-col items-center justify-evenly w-46 py-5 px-9 rounded mr-5 mt-4 shadow-sm duration-200">
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Metiers 1
                  </Link>
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields">
                    Metiers 2
                  </Link>
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Metiers 3
                  </Link>
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Metiers 4
                  </Link>
                </span>
              )}
            </li>
            <li className="p-3 hover:border-b-4 rounded duration-200">
              <Link to="/contact">Contact</Link>
            </li>
          </ul>
        </nav>
      </aside>
    </section>
  );
}

export default NavBar;
