import React, { useState } from 'react'
import list from './data'
import Resulte from './Result'

class SearchBar extends React.Component{
  constructor(){
    super()
    this.state={search:"",filtered:[],isRight:false}
  }
  handleFilter(){
    this.state.search=="fruit"||this.state.search=="legume"?this.setState(()=>{this.state.filtered=list.filter((a)=>a.type==this.state.search)}):this.setState(()=>{this.state.isRight=!this.state.isRight})
    console.log(this.state.search)

  }
  render(){
    return(
      <form >
          <input type="text" name='search' value={this.state.search} onChange={(e)=>{this.setState(e.target.value)}} />
             <button>Click</button>
      </form>
    )
}
}
export default SearchBar
