import { faFacebook, faFacebookSquare, faInstagramSquare, faTwitterSquare } from '@fortawesome/free-brands-svg-icons'
import { faAngleDown, faAngleUp, faArrowDown, faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React, { useState , useEffect } from 'react'
import { Link } from 'react-router-dom'
function NavBar() {
  const[hover,setHover]=useState(false)
  const [showDropdown, setShowDropdown] = useState(false);
  const handleMouseIn=()=>{
    setHover(true)
  }
  const handleMouseOut=()=>{
    setHover(false)
  }

  useEffect(() => {
    if (hover) {
      const timeoutId = setTimeout(() => {
        setShowDropdown(true);
      }, 400);

      return () => {
        clearTimeout(timeoutId);
      };
    } else {
      setShowDropdown(false);
    }
  }, [hover]);

  return (
    <section className='bg-blue-500 flex justify-between items-center h-32 text-white'>
      <span className='ml-10 flex items-center   '>
      <img className='ml-3 pt-2 h-24 w-52' src="./images/logo_2.png" alt="./images/logo_0.png" />
        <img className='h-20 w-20' src="./images/logo_0.png" alt="./images/logo_0.png" />
        <img className='ml-3 pt-2 h-28 w-56' src="./images/logo_1.png" alt="./images/logo_0.png" />
      </span>

        <nav className='text-2xl flex flex-col items-end justify-between'>
          <span className='flex text-lg items-center'>
            <p className=''>Nos Reseaux sociaux :</p>
            <FontAwesomeIcon className="px-2" icon={faFacebookSquare}/>
            <FontAwesomeIcon className="px-2" icon={faTwitterSquare}/>
            <FontAwesomeIcon className="px-2" icon={faInstagramSquare}/>
          </span>
            <ul className='flex px-3 justify-between mt-6 mr-10'>
              <li className='px-3'><Link to="/">Home</Link></li>
              <li className='px-3' ><Link to="/fields">Notre metiers</Link></li>
              <li className='px-3 cursor-pointer' onMouseEnter={handleMouseIn} onMouseLeave={handleMouseOut}  >
                <p>Metiers <FontAwesomeIcon className="text-lg" icon={showDropdown?faAngleDown:faAngleUp}/> </p>
                {showDropdown&&  <span className='bg-blue-300 absolute flex flex-col items-center justify-evenly w-36 px-9 rounded  shadow-sm duration-200'>
                    <Link className='py-1' to="/fields">gg</Link>
                    <Link className='py-1' to="/fields">gg</Link>
                    <Link className='py-1' to="/fields">gg</Link>
                    <Link className='py-1' to="/fields">gg</Link>
                </span>}
              </li>
              <li className='px-3'><Link to="/contact">Contact</Link></li>
            </ul>
        </nav>
    </section>
  )
}

export default NavBar
