import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
  const [hover, setHover] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleMouseIn = () => {
    setHover(true);
  };

  const handleMouseOut = () => {
    setHover(false);
  };

  useEffect(() => {
    if (hover) {
      const timeoutId = setTimeout(() => {
        setShowDropdown(true);
      }, 300);

      return () => {
        clearTimeout(timeoutId);
      };
    } else {
      setShowDropdown(false);
    }
  }, [hover]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    if (window.scrollY > 100) {
      setIsScrolled(true);
    } else {
      setIsScrolled(false);
    }
  };

  return (
    <section className='sticky'>
      {!isScrolled && (
        <aside className={`bg-slate-100 transform opacity-${!isScrolled ? 100 : 0} transition-opacity duration-300 fixed top-0 w-full`}>
          {/* Social Icons */}
        </aside>
      )}
      <aside
        className={`bg-gradient-to-r from-cyan-500 to-blue-500 flex flex-col md:flex-row justify-between items-center h-auto md:h-16 text-white ${
          isScrolled ? 'fixed top-0 duration-150' : 'fixed top-8 duration-150'
        } w-full shadow-md`}
      >
        <div className="ml-3 md:ml-0 md:mr-3 flex items-center">
          <img
            className="h-16 md:h-20 lg:h-24"
            src="./images/logo_2.png"
            alt="./images/logo_0.png"
          />
          <img
            className="h-16 md:h-18 lg:h-20"
            src="./images/logo_0.png"
            alt="./images/logo_0.png"
          />
          <img
            className="h-20 md:h-24 lg:h-28"
            src="./images/logo_1.png"
            alt="./images/logo_0.png"
          />
        </div>
        <nav className="text-2xl flex flex-row items-center justify-between">
          <ul className="flex flex-col md:flex-row px-3 justify-between items-center md:mr-10 font-bold">
            <li className="p-3 hover:border-b-4 rounded duration-100">
              <Link to="/">Home</Link>
            </li>
            <li className="p-3 hover:border-b-4 rounded duration-100">
              <Link to="/fields">Notre métiers</Link>
            </li>
            <li
              className="p-3 hover:border-b-4 rounded duration-100 cursor-pointer md:hover:border-b-0"
              onMouseEnter={handleMouseIn}
              onMouseLeave={handleMouseOut}
            >
              <p>
                Métiers{' '}
                <FontAwesomeIcon
                  className="text-lg"
                  icon={showDropdown ? faAngleDown : faAngleUp}
                />{' '}
              </p>
              {showDropdown && (
                <span className="bg-gray-600 absolute flex flex-col items-center justify-evenly w-46 py-5 px-9 rounded mr-5 mt-4 shadow-sm duration-200">
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Métiers 1
                  </Link>
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Métiers 2
                  </Link>
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Métiers 3
                  </Link>
                  <Link
                    className="p-2 hover:border-b-4 rounded duration-100 text-lg"
                    to="/fields"
                  >
                    Métiers 4
                  </Link>
                </span>
              )}
            </li>
            <li className="p-3 hover:border-b-4 rounded duration-200">
              <Link to="/contact">Contact</Link>
            </li>
          </ul>
        </nav>
      </aside>
    </section>
  );
}

export default NavBar;
