import { faFacebook, faFacebookSquare, faInstagramSquare, faTwitterSquare } from '@fortawesome/free-brands-svg-icons'
import { faAngleDown, faAngleUp, faArrowDown, faArrowUp } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import React, { useState , useEffect } from 'react'
import { Link } from 'react-router-dom'
function NavBar() {
  const[hover,setHover]=useState(false)
  const [showDropdown, setShowDropdown] = useState(false);
  const handleMouseIn=()=>{
    setHover(true)
  }
  const handleMouseOut=()=>{
    setHover(false)
  }

  useEffect(() => {
    if (hover) {
      const timeoutId = setTimeout(() => {
        setShowDropdown(true);
      }, 300);

      return () => {
        clearTimeout(timeoutId);
      };
    } else {
      setShowDropdown(false);
    }
  }, [hover]);

  return (
    <section className='bg-blue-500 flex justify-between items-center h-24 text-white'>
      <span className='ml-10 flex items-center   '>
      <img className='ml-3 pt-2 h-20 w-48' src="./images/logo_2.png" alt="./images/logo_0.png" />
        <img className='h-18 w-20' src="./images/logo_0.png" alt="./images/logo_0.png" />
        <img className='ml-3 pt-2 h-24 w-50' src="./images/logo_1.png" alt="./images/logo_0.png" />
      </span>

        <nav className='text-2xl flex flex-row items-center justify-between'>

            <ul className='flex px-3 justify-between items-center  mr-10 font-bold '>
              <li className='p-3 hover:bg-blue-300 rounded duration-200'><Link to="/">Home</Link></li>
              <li className='p-3 hover:bg-blue-300 rounded duration-200' ><Link to="/fields">Notre metiers</Link></li>
              <li className='p-3 hover:bg-blue-300 rounded duration-200 cursor-pointer' onMouseEnter={handleMouseIn} onMouseLeave={handleMouseOut}  >
                <p>Metiers <FontAwesomeIcon className="text-lg" icon={showDropdown?faAngleDown:faAngleUp}/> </p>
                {showDropdown&&  <span className='bg-gray-600 absolute flex flex-col items-center justify-evenly w-46 py-5 px-9 rounded  shadow-sm duration-200'>
                    <Link className='p-2 hover:bg-blue-300 rounded duration-200 text-lg' to="/fields">Metiers 1</Link>
                    <Link className='p-2 hover:bg-blue-300 rounded duration-200 text-lg' to="/fields">Metiers 2</Link>
                    <Link className='p-2 hover:bg-blue-300 rounded duration-200 text-lg' to="/fields">Metiers 3</Link>
                    <Link className='p-2 hover:bg-blue-300 rounded duration-200 text-lg' to="/fields">Metiers 4</Link>
                </span>}
              </li>
                <li className='p-3 hover:bg-blue-300 rounded duration-200'><Link  to="/contact">Contact</Link></li>
            </ul>

            <span className='flex text-1xl flex-col py-1 items-center'>
            <FontAwesomeIcon className="p-2" icon={faFacebookSquare}/>
            <FontAwesomeIcon className="p-2" icon={faTwitterSquare}/>
            <FontAwesomeIcon className="p-2" icon={faInstagramSquare}/>
          </span>

        </nav>
    </section>
  )
}

export default NavBar
